window.App=window.App||{},window.App.ProfilePlaceholders={initialize:function(){let nodes=document.querySelectorAll("[data-dt-placeholder-profile]");function applyProfilePlaceholders(){let updates=[];nodes.forEach(div=>{var name=div.getAttribute("data-dt-placeholder-profile");if(name){let style="glass";switch(parseInt(div.getAttribute("data-dt-placeholder-profile-type"))||2){case 1:style="initials";break;case 2:style="glass";break;case 3:style="shapes";break;case 4:style="identicon";break;case 5:style="bottts-neutral"}name=encodeURIComponent(name.trim()),name=`https://api.dicebear.com/9.x/${style}/svg?seed=`+name;updates.push({div:div,url:name})}});for(let i=0;i<updates.length;i++){var{div,url}=updates[i];div.style.backgroundImage=`url("${url}")`,div.style.backgroundSize="cover",div.style.backgroundPosition="center",div.style.backgroundRepeat="no-repeat"}}0<nodes.length&&("undefined"!=typeof requestIdleCallback?requestIdleCallback(applyProfilePlaceholders,{timeout:500}):setTimeout(applyProfilePlaceholders,0))}};